// Login.js
import React from "react";
import { VStack, Heading, Button, ButtonGroup } from "@chakra-ui/react";
import { Form, Formik } from "formik";
import * as Yup from "yup";
import TextField from "./TextField";

export const Login = () => {
  return (
    <Formik
      initialValues={{ username: "", password: "" }}
      validationSchema={Yup.object({
        username: Yup.string().required("Username required!").min(6, "Username too short!").max(28, "Username too long!"),
        password: Yup.string().required("Password required!").min(6, "Password too short!").max(28, "Password too long!"),
      })}
      onSubmit={(values, action) => {
        alert(JSON.stringify(values, null, 2));
        action.resetForm();
      }}
    >
      {(formik) => (
        <VStack as={Form} w={{ base: "90%", md: "500px" }} m="auto" justify="center" h="100vh" spacing="1rem">
          <Heading>Log In</Heading>
          <TextField name="username" placeholder="Enter username" autoComplete="off" label="Username" formik={formik} />
          <TextField name="password" placeholder="Enter password" autoComplete="off" label="Password" formik={formik} />
          <ButtonGroup>
            <Button colorScheme="teal" type="submit">
              Login
            </Button>
          </ButtonGroup>
        </VStack>
      )}
    </Formik>
  );
};
