import {
  VStack,
  ButtonGroup,
  FormControl,
  FormLabel,
  Button,
  FormErrorMessage,
  Input,
} from "@chakra-ui/react";
import React from "react";

export const Login = () => {
  return (
    <VStack
      as="form"
      w={{ base: "90%", md: "500px" }}
      m="auto"
      justify="center"
      h="100vh"
      spacing="1rem"
    >
      <FormControl>
        <FormLabel>Username</FormLabel>
        <Input name="username" placeholder="Enter username" autoComplete="off"/>
        <FormErrorMessage>Invalid username</FormErrorMessage>
      </FormControl>
    </VStack>
  );
};
