import {
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
} from "@chakra-ui/modal";
import { Button, ModalOverlay } from "@chakra-ui/react";
import { TextField } from "../TextField";
import { Formik } from "formik";

const AddFriendModal = ({ isOpen, onClose }) => {
  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <ModalOverlay />
      <ModalContent>
        <ModalHeader>Add a friend!</ModalHeader>
        <ModalCloseButton />
        <Formik></Formik>
        <ModalBody>
          <TextField
            label="Friend's name"
            placeholder="Enter friend's username..."
            autoComplete="off"
            name="friendName"
          />
        </ModalBody>
        <ModalFooter>
          <Button colorScheme="blue" onClick={onClose} type="submit">
            Submit
          </Button>
        </ModalFooter>
      </ModalContent>
    </Modal>
  );
};

export default AddFriendModal;
