import React, { useContext } from "react";
import {
  Button,
  Circle,
  Divider,
  HStack,
  Heading,
  Tab,
  Text,
  TabList,
  VStack,
  useDisclosure,
} from "@chakra-ui/react";
import { ChatIcon } from "@chakra-ui/icons";
import { FriendContext } from "./Home";
import AddFriendModal from "./AddFriendModal";
import FriendList from "./FriendList"; // Import the FriendList component

const SideBar = () => {
  const { friendList } = useContext(FriendContext);
  const {
    isOpen: modalIsOpen,
    onOpen: modalOnOpen,
    onClose: modalOnClose,
  } = useDisclosure();

  return (
    <>
      <VStack py="1.4rem">
        <HStack justify="space-evenly" w="100%">
          <Heading size="md">Add Friend</Heading>
          <Button onClick={modalOnOpen}>
            <ChatIcon />
          </Button>
        </HStack>
        <Divider />
        <FriendList friendList={friendList} /> {/* Render the FriendList component */}
      </VStack>
      <AddFriendModal isOpen={modalIsOpen} onClose={modalOnClose} />
    </>
  );
};

export default SideBar;
